// src/AboutMe.jsx
import React from 'react';

const AboutMe = ({ darkMode }) => {
  return (
    <section
      id="about"
      className={`py-16 px-6 md:px-20 transition-colors duration-300 ${
        darkMode ? 'bg-gray-900 text-white' : 'bg-white text-gray-800'
      }`}
    >
      <div className="text-center mb-10">
        <h2 className="text-3xl font-bold underline underline-offset-8 decoration-blue-500 mb-4">
          About Me
        </h2>
        <p className="text-lg max-w-xl mx-auto">
          Know more about who I am, what I do, and what drives me.
        </p>
      </div>

      <div
        className={`max-w-3xl mx-auto flex flex-col md:flex-row items-center gap-6 p-6 rounded-2xl shadow-lg ${
          darkMode ? 'bg-gray-800' : 'bg-gray-100'
        }`}
      >
        <img
          src="https://avatars.githubusercontent.com/u/101703568?v=4"
          alt="Harshwardhan Avatar"
          className="w-32 h-32 rounded-full object-cover border-4 border-blue-500"
        />
        <div>
          <h3 className="text-2xl font-semibold mb-1">Harshwardhan Sahu</h3>
          <p className="text-sm text-blue-500 mb-2">B.Tech CSE | Quantum University</p>
          <p className="text-sm leading-6">
            I’m passionate about building AI-driven tools, solving real-world
            problems, and learning new technologies. I've worked on smart travel
            planners, educational apps, and multilingual bots. Let’s build the future!
          </p>
        </div>
      </div>
    </section>
  );
};

export default AboutMe;
