import React from 'react';

const projects = [
  {
    title: 'AI Travel Planner',
    description: 'Built with Streamlit, Gemini API, and RapidAPI for personalized travel itineraries.',
    link: 'https://github.com/Harshwardhan0270/Travel-Planner',
  },
  {
    title: 'Slack Intern Assignment',
    description: 'An interactive Slack-style interface using HTML, CSS, JS.',
    link: 'https://github.com/Harshwardhan0270/slack-intern-assignment',
  },
  {
    title: 'Portfolio Website',
    description: 'A responsive personal website built with React and Tailwind CSS.',
    link: 'https://github.com/Harshwardhan0270',
  },
];

const Projects = ({ darkMode }) => {
  return (
    <section
      id="projects"
      className={`py-16 px-6 md:px-20 transition-colors duration-300 ${
        darkMode ? 'bg-gray-800 text-white' : 'bg-white text-gray-900'
      }`}
    >
      <div className="text-center mb-10">
        <h2 className="text-3xl font-bold underline underline-offset-8 decoration-purple-500 mb-4">
          Projects
        </h2>
        <p className="text-lg max-w-xl mx-auto">
          A selection of my favorite personal and open-source projects.
        </p>
      </div>

      <div className="grid gap-8 md:grid-cols-3">
        {projects.map((project, index) => (
          <div
            key={index}
            className={`p-6 rounded-xl shadow-lg hover:shadow-xl transform transition hover:scale-105 ${
              darkMode ? 'bg-gray-700' : 'bg-gray-100'
            }`}
          >
            <h3 className="text-xl font-semibold text-blue-600 mb-2">{project.title}</h3>
            <p className="text-sm mb-4">{project.description}</p>
            <a
              href={project.link}
              target="_blank"
              rel="noopener noreferrer"
              className="text-blue-500 font-medium hover:underline"
            >
              View on GitHub
            </a>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Projects;
