// src/Home.jsx
import React from "react";
import { Typewriter } from "react-simple-typewriter";
import { FaGithub, FaLinkedin, FaEnvelope } from "react-icons/fa";

const Home = ({ darkMode }) => {
  return (
    <section
      id="home"
      className={`min-h-screen flex flex-col justify-center items-center text-center px-6 transition-colors duration-300 ${
        darkMode ? "bg-gray-900 text-white" : "bg-white text-gray-800"
      }`}
    >
      {/* Avatar */}
      <img
        src="https://avatars.githubusercontent.com/u/101703568?v=4"
        alt="Harshwardhan"
        className="w-32 h-32 rounded-full border-4 border-blue-500 shadow-md mb-6 animate-fade-in"
      />

      {/* Heading */}
      <h1 className="text-4xl sm:text-6xl font-bold mb-4 animate-slide-in">
        Hi, I'm <span className="text-blue-500">Harshwardhan Sahu</span>
      </h1>

      <h2 className="text-xl sm:text-2xl mb-6 text-blue-400 font-medium animate-fade-in-slow">
        <Typewriter
          words={["Full Stack Developer", "Open Source Enthusiast", "Tech Explorer"]}
          loop
          cursor
          cursorStyle="_"
          typeSpeed={70}
          deleteSpeed={50}
          delaySpeed={1000}
        />
      </h2>

      {/* Description */}
      <p className="max-w-xl mb-8 text-base sm:text-lg leading-relaxed animate-fade-in-slow">
        I love building fast, accessible, and modern web applications. Currently exploring AI agents,
        multilingual bots, and interactive UIs.
      </p>

      {/* Buttons */}
      <div className="flex flex-col sm:flex-row gap-4 mb-8">
        <a
          href=""
          download
          className="px-6 py-3 bg-blue-600 text-white rounded-xl font-semibold shadow-md hover:bg-blue-700 transition"
        >
          ðŸ“„ Download Resume
        </a>
        <a
          href="#contact"
          className={`px-6 py-3 ${
            darkMode ? "bg-white text-gray-900" : "bg-gray-900 text-white"
          } rounded-xl font-semibold shadow-md hover:opacity-90 transition`}
        >
          ðŸš€ Hire Me
        </a>
      </div>

      {/* Social Icons */}
      <div className="flex space-x-6 text-2xl">
        <a
          href="https://github.com/Harshwardhan0270"
          target="_blank"
          rel="noopener noreferrer"
          className="hover:text-blue-500 transition-colors duration-200"
        >
          <FaGithub />
        </a>
        <a
          href="https://linkedin.com/in/harshwardhansahu"
          target="_blank"
          rel="noopener noreferrer"
          className="hover:text-blue-500 transition-colors duration-200"
        >
          <FaLinkedin />
        </a>
        <a
          href="mailto:harshwardhansahu@gmail.com"
          className="hover:text-blue-500 transition-colors duration-200"
        >
          <FaEnvelope />
        </a>
      </div>
    </section>
  );
};

export default Home;
