// src/Projects.jsx
import React from 'react';
import { FaExternalLinkAlt, FaGithub } from 'react-icons/fa';

const projects = [
  {
    title: 'AI Travel Planner',
    description:
      'An AI-based tool that generates personalized travel itineraries using Gemini API, RapidAPI, and Folium.',
    tech: ['Python', 'Streamlit', 'LangChain', 'Firebase'],
    live: 'https://ai-travel-planner.streamlit.app',
    github: 'https://github.com/Harshwardhan0270/Travel-Planner',
  },
  {
    title: 'Multilingual WhatsApp Bot',
    description:
      'A smart chatbot for Bharat that supports multiple Indian languages and delivers instant replies using LangChain.',
    tech: ['Python', 'Twilio', 'LangChain', 'Flask'],
    live: '',
    github: 'https://github.com/Harshwardhan0270',
  },
  {
    title: 'Bloom’s Taxonomy Question Generator',
    description:
      'An educational tool that classifies and generates questions using Bloom’s cognitive levels.',
    tech: ['Python', 'Streamlit', 'NLP', 'LangChain'],
    live: '',
    github: 'https://github.com/Harshwardhan0270',
  },
];

const Projects = ({ darkMode }) => {
  return (
    <section
      id="projects"
      className={`py-16 px-6 md:px-20 transition-colors duration-300 ${
        darkMode ? 'bg-gray-900 text-white' : 'bg-white text-gray-900'
      }`}
    >
      <div className="text-center mb-10">
        <h2 className="text-3xl font-bold underline underline-offset-8 decoration-blue-500 mb-4">
          My Projects
        </h2>
        <p className="text-lg max-w-xl mx-auto">
          A collection of my personal and academic projects.
        </p>
      </div>

      <div className="grid gap-8 md:grid-cols-2 lg:grid-cols-3">
        {projects.map((project, index) => (
          <div
            key={index}
            className={`rounded-2xl p-6 shadow-lg transition transform hover:scale-105 ${
              darkMode ? 'bg-gray-800' : 'bg-gray-100'
            }`}
          >
            <h3 className="text-xl font-semibold mb-2">{project.title}</h3>
            <p className="text-sm mb-3">{project.description}</p>
            <div className="flex flex-wrap gap-2 mb-4">
              {project.tech.map((tech, idx) => (
                <span
                  key={idx}
                  className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded-full font-medium"
                >
                  {tech}
                </span>
              ))}
            </div>
            <div className="flex gap-4">
              {project.live && (
                <a
                  href={project.live}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-sm flex items-center gap-1 text-blue-600 hover:underline"
                >
                  <FaExternalLinkAlt /> Live Demo
                </a>
              )}
              <a
                href={project.github}
                target="_blank"
                rel="noopener noreferrer"
                className="text-sm flex items-center gap-1 text-gray-600 hover:underline"
              >
                <FaGithub /> GitHub
              </a>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Projects;
