// src/Skills.jsx
import React from 'react';
import {
  FaCode, FaDatabase, FaCloud, FaPaintBrush, FaTools, FaLaptopCode
} from 'react-icons/fa';

const skillsData = [
  {
    title: 'Programming Languages',
    icon: <FaCode size={24} />,
    list: ['Java', 'Python', 'JavaScript', 'C++'],
  },
  {
    title: 'Frontend Development',
    icon: <FaLaptopCode size={24} />,
    list: ['HTML', 'CSS', 'React', 'Tailwind CSS', 'TypeScript'],
  },
  {
    title: 'Backend Development',
    icon: <FaCode size={24} />,
    list: ['Node.js', 'Express.js', 'REST API'],
  },
  {
    title: 'Database & Cloud',
    icon: <FaDatabase size={24} />,
    list: ['MongoDB', 'Firebase', 'MySQL'],
  },
  {
    title: 'Tools & Platforms',
    icon: <FaTools size={24} />,
    list: ['Git', 'GitHub', 'VS Code', 'Postman', 'Netlify', 'Vercel'],
  },
  {
    title: 'UI/UX & Design',
    icon: <FaPaintBrush size={24} />,
    list: ['Figma', 'Canva', 'Adobe XD'],
  },
];

const Skills = ({ darkMode }) => {
  return (
    <section
      id="skills"
      className={`py-16 px-6 md:px-20 transition-colors duration-300 ${
        darkMode ? 'bg-gray-900 text-white' : 'bg-white text-gray-800'
      }`}
    >
      <div className="text-center mb-10">
        <h2 className="text-3xl font-bold underline underline-offset-8 decoration-blue-500 mb-4">
          My Skills
        </h2>
        <p className="text-lg max-w-xl mx-auto">
          Here are the technologies, tools, and platforms I work with.
        </p>
      </div>

      <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-8">
        {skillsData.map((skill, index) => (
          <div
            key={index}
            className={`p-6 rounded-2xl shadow-lg hover:scale-105 transform transition ${
              darkMode ? 'bg-gray-800' : 'bg-gray-100'
            }`}
          >
            <div className="flex items-center gap-3 mb-4 text-blue-600">
              {skill.icon}
              <h3 className="text-xl font-semibold">{skill.title}</h3>
            </div>
            <ul className="list-disc list-inside space-y-1 text-sm pl-1">
              {skill.list.map((tech, idx) => (
                <li key={idx}>{tech}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Skills;
