// src/Projects.jsx
import React from 'react';
import { FaGithub, FaExternalLinkAlt } from 'react-icons/fa';

const projects = [
  {
    title: 'AI Travel Planner',
    description: 'An intelligent travel itinerary generator using Streamlit and Gemini API.',
    techStack: ['Python', 'Streamlit', 'Gemini API', 'Folium'],
    github: 'https://github.com/Harshwardhan0270/Travel-Planner',
    live: '',
  },
  {
    title: 'Slack Bot Assignment',
    description: 'Custom Slack bot built for internship assignment using Python.',
    techStack: ['Python', 'Slack API', 'Flask'],
    github: 'https://github.com/Harshwardhan0270/slack-intern-assignment',
    live: '',
  },
  // Add more projects here...
];

const Projects = ({ darkMode }) => {
  return (
    <section
      id="projects"
      className={`py-16 px-6 md:px-20 transition-colors duration-300 ${
        darkMode ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-800'
      }`}
    >
      <div className="text-center mb-10">
        <h2 className="text-3xl font-bold underline underline-offset-8 decoration-blue-500 mb-4">
          Projects
        </h2>
        <p className="text-lg max-w-xl mx-auto">
          These are some of the projects Iâ€™ve built recently.
        </p>
      </div>

      <div className="grid md:grid-cols-2 gap-8">
        {projects.map((project, idx) => (
          <div
            key={idx}
            className={`p-6 rounded-xl shadow-lg hover:shadow-2xl transition duration-300 transform hover:-translate-y-1 ${
              darkMode ? 'bg-gray-800' : 'bg-white'
            }`}
          >
            <h3 className="text-xl font-bold mb-2 text-blue-500">{project.title}</h3>
            <p className="mb-3 text-sm">{project.description}</p>
            <div className="flex flex-wrap gap-2 mb-3">
              {project.techStack.map((tech, i) => (
                <span
                  key={i}
                  className="bg-blue-100 text-blue-700 px-2 py-1 rounded text-xs"
                >
                  {tech}
                </span>
              ))}
            </div>
            <div className="flex gap-4 text-blue-500">
              {project.github && (
                <a href={project.github} target="_blank" rel="noopener noreferrer">
                  <FaGithub size={18} />
                </a>
              )}
              {project.live && (
                <a href={project.live} target="_blank" rel="noopener noreferrer">
                  <FaExternalLinkAlt size={18} />
                </a>
              )}
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Projects;
