// src/Home.jsx
import React from "react";
import { Typewriter } from "react-simple-typewriter";
import { FaGithub, FaLinkedin, FaEnvelope } from "react-icons/fa";

const Home = ({ darkMode }) => {
  return (
    <section
      id="home"
      className={`min-h-screen flex flex-col justify-center items-center text-center px-6 ${
        darkMode ? "bg-gray-900 text-white" : "bg-white text-gray-800"
      }`}
    >
      <h1 className="text-4xl sm:text-6xl font-bold mb-4">
        Hi, I'm <span className="text-blue-500">Harshwardhan Sahu</span>
      </h1>

      <h2 className="text-xl sm:text-2xl mb-6">
        <Typewriter
          words={["Full Stack Developer", "Open Source Enthusiast", "Tech Explorer"]}
          loop
          cursor
          cursorStyle="_"
          typeSpeed={70}
          deleteSpeed={50}
          delaySpeed={1000}
        />
      </h2>

      <p className="max-w-xl mb-8">
        I'm passionate about building beautiful, performant web applications and learning modern tech.
      </p>

      <div className="flex space-x-6 text-2xl">
        <a href="https://github.com/Harshwardhan0270" target="_blank" rel="noopener noreferrer">
          <FaGithub />
        </a>
        <a href="https://linkedin.com/in/harshwardhansahu" target="_blank" rel="noopener noreferrer">
          <FaLinkedin />
        </a>
        <a href="mailto:harshwardhansahu@gmail.com">
          <FaEnvelope />
        </a>
      </div>
    </section>
  );
};

export default Home;
